window.translation = $.extend(window.translation || {}, {
"20:13" : {s: 20, a: 13, e: "I have chosen you, so listen to what is being revealed.", b: "এবং আমি তোমাকে মনোনীত করেছি, অতএব যা প্রত্যাদেশ করা হচ্ছে, তা শুনতে থাক।"},
"20:14" : {s: 20, a: 14, e: "I am God; there is no god but Me. So worship Me and keep up the prayer so that you remember Me.", b: "আমিই আল্লাহ আমি ব্যতীত কোন ইলাহ নেই। অতএব আমার এবাদত কর এবং আমার স্মরণার্থে নামায কায়েম কর।"},
"20:15" : {s: 20, a: 15, e: "The Hour is coming&ndash;&ndash;though I choose to keep it hidden&ndash;&ndash;for each soul to be rewarded for its labour.", b: "কেয়ামত অবশ্যই আসবে, আমি তা গোপন রাখতে চাই; যাতে প্রত্যেকেই তার কর্মানুযায়ী ফল লাভ করে।"},
"20:16" : {s: 20, a: 16, e: "Do not let anyone who does not believe in it and follows his own desires distract you from it, and so bring you to ruin.&rsquo;", b: "সুতরাং যে ব্যক্তি কেয়ামতে বিশ্বাস রাখে না এবং নিজ খাহেশের অনুসরণ করে, সে যেন তোমাকে তা থেকে নিবৃত্ত না করে। নিবৃত্ত হলে তুমি ধবংস হয়ে যাবে।"},
"20:17" : {s: 20, a: 17, e: "&lsquo;Moses, what is that in your right hand?&rsquo;", b: "হে মূসা, তোমার ডানহাতে ওটা কি?"},
"20:18" : {s: 20, a: 18, e: "&lsquo;It is my staff,&rsquo; he said, &lsquo;I lean on it; restrain my sheep with it; I also have other uses for it.&rsquo;", b: "তিনি বললেনঃ এটা আমার লাঠি, আমি এর উপর ভর দেই এবং এর দ্বারা আমার ছাগপালের জন্যে বৃক্ষপত্র ঝেড়ে ফেলি এবং এতে আমার অন্যান্য কাজ ও চলে।"},
"20:19" : {s: 20, a: 19, e: "God said, &lsquo;Throw it down, Moses.&rsquo;", b: "আল্লাহ বললেনঃ হে মূসা, তুমি ওটা নিক্ষেপ কর।"},
"20:20" : {s: 20, a: 20, e: "He threw it down and- lo and behold!- it became a fast-moving snake.", b: "অতঃপর তিনি তা নিক্ষেপ করলেন, অমনি তা সাপ হয়ে ছুটাছুটি করতে লাগল।"},
"20:21" : {s: 20, a: 21, e: "He said, &lsquo;Pick it up without fear: We shall turn it back into its former state.", b: "আল্লাহ বললেনঃ তুমি তাকে ধর এবং ভয় করো না, আমি এখনি একে পূর্বাবস্থায় ফিরিয়ে দেব।"},
"20:22" : {s: 20, a: 22, e: "Now place your hand under your armpit and it will come out white, though unharmed: that is another sign.", b: "তোমার হাত বগলে রাখ, তা বের হয়ে আসবে নির্মল উজ্জ্বল হয়ে অন্য এক নিদর্শন রূপে; কোন দোষ ছাড়াই।"},
"20:23" : {s: 20, a: 23, e: "We do this to show you some of Our greatest signs.", b: "এটা এজন্যে যে, আমি আমার বিরাট নিদর্শনাবলীর কিছু তোমাকে দেখাই।"},
"20:24" : {s: 20, a: 24, e: "Go to Pharaoh, for he has truly become a tyrant.&rsquo;", b: "ফেরাউনের নিকট যাও, সে দারুণ উদ্ধত হয়ে গেছে।"},
"20:25" : {s: 20, a: 25, e: "Moses said, &lsquo;Lord, lift up my heart", b: "মূসা বললেনঃ হে আমার পালনকর্তা আমার বক্ষ প্রশস্ত করে দিন।"},
"20:26" : {s: 20, a: 26, e: "and ease my task for me.", b: "এবং আমার কাজ সহজ করে দিন।"},
"20:27" : {s: 20, a: 27, e: "Untie my tongue,", b: "এবং আমার জিহবা থেকে জড়তা দূর করে দিন।"},
"20:28" : {s: 20, a: 28, e: "so that they may understand my words,", b: "যাতে তারা আমার কথা বুঝতে পারে।"},
"20:29" : {s: 20, a: 29, e: "and give me a helper from my family,", b: "এবং আমার পরিবারবর্গের মধ্য থেকে আমার একজন সাহায্যকারী করে দিন।"},
"20:30" : {s: 20, a: 30, e: "my brother Aaron-", b: "আমার ভাই হারুনকে।"},
"20:31" : {s: 20, a: 31, e: "augment my strength through him.", b: "তার মাধ্যমে আমার কোমর মজবুত করুন।"},
"20:32" : {s: 20, a: 32, e: "Let him share my task", b: "এবং তাকে আমার কাজে অংশীদার করুন।"},
"20:33" : {s: 20, a: 33, e: "so that we can glorify You much", b: "যাতে আমরা বেশী করে আপনার পবিত্রতা ও মহিমা ঘোষনা করতে পারি।"},
"20:34" : {s: 20, a: 34, e: "and remember You often:", b: "এবং বেশী পরিমাণে আপনাকে স্মরণ করতে পারি।"},
"20:35" : {s: 20, a: 35, e: "You are always watching over us.&rsquo;", b: "আপনি তো আমাদের অবস্থা সবই দেখছেন।"},
"20:36" : {s: 20, a: 36, e: "God said, &lsquo;Moses, your request is granted.", b: "আল্লাহ বললেনঃ হে মূসা, তুমি যা চেয়েছ তা তোমাকে দেয়া হল।"},
"20:37" : {s: 20, a: 37, e: "Indeed We showed you favour before.", b: "আমি তোমার প্রতি আরও একবার অনুগ্রহ করেছিলাম।"},
});
