window.translation = $.extend(window.translation || {}, {
"23:1" : {s: 23, a: 1, e: "[How] prosperous are the believers!", b: "মুমিনগণ সফলকাম হয়ে গেছে,", t: "بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ قَدْ أَفْلَحَ ٱلْمُؤْمِنُونَ"},
"23:2" : {s: 23, a: 2, e: "Those who pray humbly,", b: "যারা নিজেদের নামাযে বিনয়-নম্র;", t: "ٱلَّذِينَ هُمْ فِى صَلَاتِهِمْ خَٰشِعُونَ"},
"23:3" : {s: 23, a: 3, e: "who shun idle talk,", b: "যারা অনর্থক কথা-বার্তায় নির্লিপ্ত,", t: "وَٱلَّذِينَ هُمْ عَنِ ٱللَّغْوِ مُعْرِضُونَ"},
"23:4" : {s: 23, a: 4, e: "who pay the prescribed alms,", b: "যারা যাকাত দান করে থাকে", t: "وَٱلَّذِينَ هُمْ لِلزَّكَوٰةِ فَٰعِلُونَ"},
"23:5" : {s: 23, a: 5, e: "who guard their chastity", b: "এবং যারা নিজেদের যৌনাঙ্গকে সংযত রাখে।", t: "وَٱلَّذِينَ هُمْ لِفُرُوجِهِمْ حَٰفِظُونَ"},
"23:6" : {s: 23, a: 6, e: "except with their spouses or their slaves&ndash;&ndash;with these they are not to blame,", b: "তবে তাদের স্ত্রী ও মালিকানাভুক্ত দাসীদের ক্ষেত্রে সংযত না রাখলে তারা তিরস্কৃত হবে না।", t: "إِلَّا عَلَىٰٓ أَزْوَٰجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَٰنُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ"},
"23:7" : {s: 23, a: 7, e: "but anyone who seeks more than this is exceeding the limits&ndash;&ndash;", b: "অতঃপর কেউ এদেরকে ছাড়া অন্যকে কামনা করলে তারা সীমালংঘনকারী হবে।", t: "فَمَنِ ٱبْتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُو۟لَٰٓئِكَ هُمُ ٱلْعَادُونَ"},
"23:8" : {s: 23, a: 8, e: "who are faithful to their trusts and pledges", b: "এবং যারা আমানত ও অঙ্গীকার সম্পর্কে হুশিয়ার থাকে।", t: "وَٱلَّذِينَ هُمْ لِأَمَٰنَٰتِهِمْ وَعَهْدِهِمْ رَٰعُونَ"},
"23:9" : {s: 23, a: 9, e: "and who keep up their prayers,", b: "এবং যারা তাদের নামাযসমূহের খবর রাখে।", t: "وَٱلَّذِينَ هُمْ عَلَىٰ صَلَوَٰتِهِمْ يُحَافِظُونَ"},
"23:10" : {s: 23, a: 10, e: "will rightly be given", b: "তারাই উত্তরাধিকার লাভ করবে।", t: "أُو۟لَٰٓئِكَ هُمُ ٱلْوَٰرِثُونَ"},
"23:11" : {s: 23, a: 11, e: "Paradise as their own, there to remain.", b: "তারা শীতল ছায়াময় উদ্যানের উত্তরাধিকার লাভ করবে। তারা তাতে চিরকাল থাকবে।", t: "ٱلَّذِينَ يَرِثُونَ ٱلْفِرْدَوْسَ هُمْ فِيهَا خَٰلِدُونَ"},
"23:12" : {s: 23, a: 12, e: "We created man from an essence of clay,", b: "আমি মানুষকে মাটির সারাংশ থেকে সৃষ্টি করেছি।", t: "وَلَقَدْ خَلَقْنَا ٱلْإِنسَٰنَ مِن سُلَٰلَةٍ مِّن طِينٍ"},
"23:13" : {s: 23, a: 13, e: "then We placed him as a drop of fluid in a safe place,", b: "অতঃপর আমি তাকে শুক্রবিন্দু রূপে এক সংরক্ষিত আধারে স্থাপন করেছি।", t: "ثُمَّ جَعَلْنَٰهُ نُطْفَةً فِى قَرَارٍ مَّكِينٍ"},
"23:14" : {s: 23, a: 14, e: "then We made that drop into a clinging form, and We made that form into a lump of flesh, and We made that lump into bones, and We clothed those bones with flesh, and later We made him into other forms&ndash;&ndash;glory be to God, the best of creators!&ndash;&ndash;", b: "এরপর আমি শুক্রবিন্দুকে জমাট রক্তরূপে সৃষ্টি করেছি, অতঃপর জমাট রক্তকে মাংসপিন্ডে পরিণত করেছি, এরপর সেই মাংসপিন্ড থেকে অস্থি সৃষ্টি করেছি, অতঃপর অস্থিকে মাংস দ্বারা আবৃত করেছি, অবশেষে তাকে নতুন রূপে দাঁড় করিয়েছি। নিপুণতম সৃষ্টিকর্তা আল্লাহ কত কল্যাণময়।", t: "ثُمَّ خَلَقْنَا ٱلنُّطْفَةَ عَلَقَةً فَخَلَقْنَا ٱلْعَلَقَةَ مُضْغَةً فَخَلَقْنَا ٱلْمُضْغَةَ عِظَٰمًا فَكَسَوْنَا ٱلْعِظَٰمَ لَحْمًا ثُمَّ أَنشَأْنَٰهُ خَلْقًا ءَاخَرَ ۚ فَتَبَارَكَ ٱللَّهُ أَحْسَنُ ٱلْخَٰلِقِينَ"},
"23:15" : {s: 23, a: 15, e: "then you will die", b: "এরপর তোমরা মৃত্যুবরণ করবে", t: "ثُمَّ إِنَّكُم بَعْدَ ذَٰلِكَ لَمَيِّتُونَ"},
"23:16" : {s: 23, a: 16, e: "and then, on the Day of Resurrection, you will be raised up again.", b: "অতঃপর কেয়ামতের দিন তোমরা পুনরুত্থিত হবে।", t: "ثُمَّ إِنَّكُمْ يَوْمَ ٱلْقِيَٰمَةِ تُبْعَثُونَ"},
"23:17" : {s: 23, a: 17, e: "We created seven levels above you: We are never unmindful of Our creation.", b: "আমি তোমাদের উপর সুপ্তপথ সৃষ্টি করেছি এবং আমি সৃষ্টি সম্বন্ধে অনবধান নই।", t: "وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَآئِقَ وَمَا كُنَّا عَنِ ٱلْخَلْقِ غَٰفِلِينَ"},
});
