"37:7:2" : {s: 37, a: 7, w: 2, r: "", t: "مِّن", l: "مِن", lc: 3067, tl: "min", lb: "মধ্য থেকে, কিছুর চেয়ে",e: "against", b: "পক্ষ হতে, থেকে", i: "dari", f: 412, rm: ""},
"37:7:3" : {s: 37, a: 7, w: 3, r: "كلل", t: "كُلِّ", l: "كُلّ", lc: 359, tl: "kulli", lb: "প্রতিটি, সব, প্রত্যেকে",e: "every", b: "সব", i: "setiap", f: 123, rm: "To lose father and child, lose direct heirs, be weary, tired, weak, have only remote relations."},
