"37:8:1" : {s: 37, a: 8, w: 1, r: "", t: "لَّا", l: "لَا", lc: 1738, tl: "La", lb: "না",e: "Not", b: "না", i: "tidak", f: 148, rm: ""},
"37:8:2" : {s: 37, a: 8, w: 2, r: "سمع", t: "يَسَّمَّعُونَ", l: "يَسَّمَّعُ", lc: 1, tl: "yassammaʿūna", lb: "শোনানো, শুনতে দেওয়া",e: "they may listen", b: "তারা কান পাতে", i: "mereka dapat mendengarkan", f: 1, rm: ""},
