"37:6:6" : {s: 37, a: 6, w: 6, r: "كوكب", t: "ٱلْكَوَاكِبِ", l: "كَوْكَب", lc: 5, tl: "l-kawākibi", lb: "তারকা",e: "(of) the stars", b: "তারকারাজি", i: "bintang-bintang", f: 1, rm: "", ag: "ال التعريف وأسم مجرور", eg: "Determiner al and Noun Genitive case", tag: "N", pos: "N", att: "GEN", qual: "", deg: "", gen: "M", num: "P", mood: "", lu: "كَوْكَبٌۭ"},
