window.translation = $.extend(window.translation || {}, {
"37:52" : {s: 37, a: 52, e: "who used to ask me, &ldquo;Do you really believe that", b: "সে বলত, তুমি কি বিশ্বাস কর যে,"},
"37:53" : {s: 37, a: 53, e: "after we die and become dust and bone, we shall be brought for judgement?&rdquo;&rsquo;", b: "আমরা যখন মরে যাব এবং মাটি ও হাড়ে পরিণত হব, তখনও কি আমরা প্রতিফল প্রাপ্ত হব?"},
"37:54" : {s: 37, a: 54, e: "Then he will say, &lsquo;Shall we look for him?&rsquo;", b: "আল্লাহ বলবেন, তোমরা কি তাকে উকি দিয়ে দেখতে চাও?"},
"37:55" : {s: 37, a: 55, e: "He will look down and see him in the midst of the Fire,", b: "অপর সে উকি দিয়ে দেখবে এবং তাকে জাহান্নামের মাঝখানে দেখতে পাবে।"},
"37:56" : {s: 37, a: 56, e: "and say to him, &lsquo;By God, you almost brought me to ruin!", b: "সে বলবে, আল্লাহর কসম, তুমি তো আমাকে প্রায় ধ্বংসই করে দিয়েছিলে।"},
"37:57" : {s: 37, a: 57, e: "Had it not been for the grace of my Lord, I too would have been taken to Hell.&rsquo;", b: "আমার পালনকর্তার অনুগ্রহ না হলে আমিও যে গ্রেফতারকৃতদের সাথেই উপস্থিত হতাম।"},
"37:58" : {s: 37, a: 58, e: "Then he will say [to his blessed companions], &lsquo;Are we never to die again after our earlier death?", b: "এখন আমাদের আর মৃত্যু হবে না।"},
"37:59" : {s: 37, a: 59, e: "Shall we never suffer?", b: "আমাদের প্রথম মৃত্যু ছাড়া এবং আমরা শাস্তি প্রাপ্তও হব না।"},
"37:60" : {s: 37, a: 60, e: "This truly is the supreme triumph!&rsquo;", b: "নিশ্চয় এই মহা সাফল্য।"},
"37:61" : {s: 37, a: 61, e: "Everyone should strive to attain this.", b: "এমন সাফল্যের জন্যে পরিশ্রমীদের পরিশ্রম করা উচিত।"},
"37:62" : {s: 37, a: 62, e: "Is this the better welcome, or the tree of Zaqqum,", b: "এই কি উত্তম আপ্যায়ন, না যাক্কুম বৃক্ষ?"},
"37:63" : {s: 37, a: 63, e: "which we have made a test for the evildoers?", b: "আমি যালেমদের জন্যে একে বিপদ করেছি।"},
"37:64" : {s: 37, a: 64, e: "This tree grows in the heart of the blazing Fire,", b: "এটি একটি বৃক্ষ, যা উদগত হয় জাহান্নামের মূলে।"},
"37:65" : {s: 37, a: 65, e: "and its fruits are like devils&rsquo; heads.", b: "এর গুচ্ছ শয়তানের মস্তকের মত।"},
"37:66" : {s: 37, a: 66, e: "They will fill their bellies eating from it;", b: "কাফেররা একে ভক্ষণ করবে এবং এর দ্বারা উদর পূর্ণ করবে।"},
"37:67" : {s: 37, a: 67, e: "then drink scalding water on top of it;", b: "তদুপরি তাদেরকে দেয়া হবে। ফুটন্ত পানির মিশ্রণ,"},
"37:68" : {s: 37, a: 68, e: "then return to the blazing Fire.", b: "অতঃপর তাদের প্রত্যাবর্তন হবে জাহান্নামের দিকে।"},
"37:69" : {s: 37, a: 69, e: "They found their forefathers astray,", b: "তারা তাদের পূর্বপুরুষদেরকে পেয়েছিল বিপথগামী।"},
"37:70" : {s: 37, a: 70, e: "and rushed to follow in their footsteps- before the disbelievers [of Mecca],", b: "অতঃপর তারা তদের পদাংক অনুসরণে তৎপর ছিল।"},
"37:71" : {s: 37, a: 71, e: "most men in the past went astray,", b: "তাদের পূর্বেও অগ্রবর্তীদের অধিকাংশ বিপথগামী হয়েছিল।"},
"37:72" : {s: 37, a: 72, e: "even though We sent messengers to warn them.", b: "আমি তাদের মধ্যে ভীতি প্রদর্শনকারী প্রেরণ করেছিলাম।"},
"37:73" : {s: 37, a: 73, e: "See how those who were warned met their end!", b: "অতএব লক্ষ্য করুন, যাদেরকে ভীতিপ্রদর্শণ করা হয়েছিল, তাদের পরিণতি কি হয়েছে।"},
"37:74" : {s: 37, a: 74, e: "Not so the true servants of God.", b: "তবে আল্লাহর বাছাই করা বান্দাদের কথা ভিন্ন।"},
"37:75" : {s: 37, a: 75, e: "Noah cried to Us, and how excellent was Our response!", b: "আর নূহ আমাকে ডেকেছিল। আর কি চমৎকারভাবে আমি তার ডাকে সাড়া দিয়েছিলাম।"},
"37:76" : {s: 37, a: 76, e: "We saved him and his people from great distress,", b: "আমি তাকে ও তার পরিবারবর্গকে এক মহাসংকট থেকে রক্ষা করেছিলাম।"},
});
