"37:178:2" : {s: 37, a: 178, w: 2, r: "", t: "عَنْهُمْ", l: "عَن", lc: 417, tl: "AAanhum", lb: "থেকে, ব্যাপারে",e: "from them", b: "তাদের", i: "dari mereka", f: 51, rm: ""},
