window.translation = $.extend(window.translation || {}, {
"37:154" : {s: 37, a: 154, e: "What is the matter with you? How do you form your judgements?", b: "তোমাদের কি হল? তোমাদের এ কেমন সিন্ধান্ত?"},
"37:155" : {s: 37, a: 155, e: "Do you not reflect?", b: "তোমরা কি অনুধাবন কর না?"},
"37:156" : {s: 37, a: 156, e: "Do you perhaps have clear authority?", b: "না কি তোমাদের কাছে সুস্পষ্ট কোন দলীল রয়েছে?"},
"37:157" : {s: 37, a: 157, e: "Bring your scriptures, if you are telling the truth.", b: "তোমরা সত্যবাদী হলে তোমাদের কিতাব আন।"},
"37:158" : {s: 37, a: 158, e: "They claim that He has kinship with the jinn, yet the jinn themselves know that they will be brought before Him.", b: "তারা আল্লাহ ও জ্বিনদের মধ্যে সম্পর্ক সাব্যস্ত করেছে, অথচ জ্বিনেরা জানে যে, তারা গ্রেফতার হয়ে আসবে।"},
"37:159" : {s: 37, a: 159, e: "God is far above what they attribute to Him-", b: "তারা যা বলে তা থেকে আল্লাহ পবিত্র।"},
"37:160" : {s: 37, a: 160, e: "the true servants of God do not do such things-", b: "তবে যারা আল্লাহর নিষ্ঠাবান বান্দা, তারা গ্রেফতার হয়ে আসবে না।"},
"37:161" : {s: 37, a: 161, e: "and neither you nor what you worship", b: "অতএব তোমরা এবং তোমরা যাদের উপাসনা কর,"},
"37:162" : {s: 37, a: 162, e: "can lure away from God any", b: "তাদের কাউকেই তোমরা আল্লাহ সম্পর্কে বিভ্রান্ত করতে পারবে না।"},
"37:163" : {s: 37, a: 163, e: "except those who will burn in Hell.", b: "শুধুমাত্র তাদের ছাড়া যারা জাহান্নামে পৌছাবে।"},
"37:164" : {s: 37, a: 164, e: "[The angels say], &lsquo;Every single one of us has his appointed place:", b: "আমাদের প্রত্যেকের জন্য রয়েছে নির্দিষ্ট স্থান।"},
"37:165" : {s: 37, a: 165, e: "we are ranged in ranks.", b: "এবং আমরাই সারিবদ্ধভাবে দন্ডায়মান থাকি।"},
"37:166" : {s: 37, a: 166, e: "We glorify God.&rsquo;", b: "এবং আমরাই আল্লাহর পবিত্রতা ঘোষণা করি।"},
"37:167" : {s: 37, a: 167, e: "[The disbelievers] used to say,", b: "তারা তো বলতঃ"},
"37:168" : {s: 37, a: 168, e: "&lsquo;If only we had a scripture like previous people,", b: "যদি আমাদের কাছে পূর্ববর্তীদের কোন উপদেশ থাকত,"},
"37:169" : {s: 37, a: 169, e: "we would be true servants of God,&rsquo;", b: "তবে আমরা অবশ্যই আল্লাহর মনোনীত বান্দা হতাম।"},
"37:170" : {s: 37, a: 170, e: "yet now they reject [the Quran]. They will soon realize.", b: "বস্তুতঃ তারা এই কোরআনকে অস্বীকার করেছে। এখন শীঘ্রই তারা জেনে নিতে পারবে,"},
"37:171" : {s: 37, a: 171, e: "Our word has already been given to Our servants the messengers:", b: "আমার রাসূল ও বান্দাগণের ব্যাপারে আমার এই বাক্য সত্য হয়েছে যে,"},
"37:172" : {s: 37, a: 172, e: "it is they who will be helped,", b: "অবশ্যই তারা সাহায্য প্রাপ্ত হয়।"},
"37:173" : {s: 37, a: 173, e: "and the ones who support Our cause will be the winners.", b: "আর আমার বাহিনীই হয় বিজয়ী।"},
"37:174" : {s: 37, a: 174, e: "So [Prophet] turn away from the disbelievers for a while.", b: "অতএব আপনি কিছুকালের জন্যে তাদেরকে উপেক্ষা করুন।"},
"37:175" : {s: 37, a: 175, e: "Watch them: they will soon see.", b: "এবং তাদেরকে দেখতে থাকুন। শীঘ্রই তারাও এর পরিণাম দেখে নেবে।"},
"37:176" : {s: 37, a: 176, e: "Do they really wish to hasten Our punishment?", b: "আমার আযাব কি তারা দ্রুত কামনা করে?"},
"37:177" : {s: 37, a: 177, e: "When it descends on their courtyards, how ter-rible that morning will be for those who were warned!", b: "অতঃপর যখন তাদের আঙ্গিনায় আযাব নাযিল হবে, তখন যাদেরকে সতর্ক করা হয়েছিল, তাদের সকাল বেলাটি হবে খুবই মন্দ।"},
"37:178" : {s: 37, a: 178, e: "[Prophet], turn away from the disbelievers for a while.", b: "আপনি কিছুকালের জন্যে তাদেরকে উপেক্ষা করুন।"},
"37:179" : {s: 37, a: 179, e: "Watch them: they will soon see.", b: "এবং দেখতে থাকুন, শীঘ্রই তারাও এর পরিণাম দেখে নেবে।"},
"37:180" : {s: 37, a: 180, e: "Your Lord, the Lord of Glory, is far above what they attribute to Him.", b: "পবিত্র আপনার পরওয়ারদেগারের সত্তা, তিনি সম্মানিত ও পবিত্র যা তারা বর্ণনা করে তা থেকে।"},
"37:181" : {s: 37, a: 181, e: "Peace be upon the messengers", b: "পয়গম্বরগণের প্রতি সালাম বর্ষিত হোক।"},
"37:182" : {s: 37, a: 182, e: "and praise be to God the Lord of all the Worlds.", b: "সমস্ত প্রশংসা বিশ্বপালক আল্লাহর নিমিত্ত।"},
});
