window.translation = $.extend(window.translation || {}, {
"56:17" : {s: 56, a: 17, e: "everlasting youths will go round among them", b: "তাদের কাছে ঘোরাফেরা করবে চির কিশোরেরা।"},
"56:18" : {s: 56, a: 18, e: "with glasses, flagons, and cups of a pure drink", b: "পানপাত্র কুঁজা ও খাঁটি সূরাপূর্ণ পেয়ালা হাতে নিয়ে,"},
"56:19" : {s: 56, a: 19, e: "that causes no headache or intoxication;", b: "যা পান করলে তাদের শিরঃপীড়া হবে না এবং বিকারগ্রস্ত ও হবে না।"},
"56:20" : {s: 56, a: 20, e: "[there will be] any fruit they choose;", b: "আর তাদের পছন্দমত ফল-মুল নিয়ে,"},
"56:21" : {s: 56, a: 21, e: "the meat of any bird they like;", b: "এবং রুচিমত পাখীর মাংস নিয়ে।"},
"56:22" : {s: 56, a: 22, e: "and beautiful companions", b: "তথায় থাকবে আনতনয়না হুরগণ,"},
"56:23" : {s: 56, a: 23, e: "like hidden pearls:", b: "আবরণে রক্ষিত মোতির ন্যায়,"},
"56:24" : {s: 56, a: 24, e: "a reward for what they used to do.", b: "তারা যা কিছু করত, তার পুরস্কারস্বরূপ।"},
"56:25" : {s: 56, a: 25, e: "They will hear no idle or sinful talk there,", b: "তারা তথায় অবান্তর ও কোন খারাপ কথা শুনবে না।"},
"56:26" : {s: 56, a: 26, e: "only clean and wholesome speech.", b: "কিন্তু শুনবে সালাম আর সালাম।"},
"56:27" : {s: 56, a: 27, e: "Those on the Right, what people they are!", b: "যারা ডান দিকে থাকবে, তারা কত ভাগ্যবান।"},
"56:28" : {s: 56, a: 28, e: "They will dwell amid thornless lote trees", b: "তারা থাকবে কাঁটাবিহীন বদরিকা বৃক্ষে।"},
"56:29" : {s: 56, a: 29, e: "and clustered acacia", b: "এবং কাঁদি কাঁদি কলায়,"},
"56:30" : {s: 56, a: 30, e: "with spreading shade,", b: "এবং দীর্ঘ ছায়ায়।"},
"56:31" : {s: 56, a: 31, e: "constantly flowing water,", b: "এবং প্রবাহিত পানিতে,"},
"56:32" : {s: 56, a: 32, e: "abundant fruits,", b: "ও প্রচুর ফল-মূলে,"},
"56:33" : {s: 56, a: 33, e: "unfailing, unforbidden,", b: "যা শেষ হবার নয় এবং নিষিদ্ধ ও নয়,"},
"56:34" : {s: 56, a: 34, e: "with incomparable companions", b: "আর থাকবে সমুন্নত শয্যায়।"},
"56:35" : {s: 56, a: 35, e: "We have specially created&ndash;&ndash;", b: "আমি জান্নাতী রমণীগণকে বিশেষরূপে সৃষ্টি করেছি।"},
"56:36" : {s: 56, a: 36, e: "virginal,", b: "অতঃপর তাদেরকে করেছি চিরকুমারী।"},
"56:37" : {s: 56, a: 37, e: "loving, of matching age&ndash;&ndash;", b: "কামিনী, সমবয়স্কা।"},
"56:38" : {s: 56, a: 38, e: "for those on the Right,", b: "ডান দিকের লোকদের জন্যে।"},
"56:39" : {s: 56, a: 39, e: "many from the past", b: "তাদের একদল হবে পূর্ববর্তীদের মধ্য থেকে।"},
"56:40" : {s: 56, a: 40, e: "and many from later generations.", b: "এবং একদল পরবর্তীদের মধ্য থেকে।"},
"56:41" : {s: 56, a: 41, e: "But those on the Left, what people they are!", b: "বামপার্শ্বস্থ লোক, কত না হতভাগা তারা।"},
"56:42" : {s: 56, a: 42, e: "They will dwell amid scorching wind and scalding water", b: "তারা থাকবে প্রখর বাষ্পে এবং উত্তপ্ত পানিতে,"},
"56:43" : {s: 56, a: 43, e: "in the shadow of black smoke,", b: "এবং ধুম্রকুঞ্জের ছায়ায়।"},
"56:44" : {s: 56, a: 44, e: "neither cool nor refreshing.", b: "যা শীতল নয় এবং আরামদায়কও নয়।"},
"56:45" : {s: 56, a: 45, e: "Before, they overindulged in luxury", b: "তারা ইতিপূর্বে স্বাচ্ছন্দ্যশীল ছিল।"},
"56:46" : {s: 56, a: 46, e: "and persisted in great sin,", b: "তারা সদাসর্বদা ঘোরতর পাপকর্মে ডুবে থাকত।"},
"56:47" : {s: 56, a: 47, e: "always saying, &lsquo;What? When we are dead and have become dust and bones, shall we then be raised up?", b: "তারা বলতঃ আমরা যখন মরে অস্থি ও মৃত্তিকায় পরিণত হয়ে যাব, তখনও কি পুনরুত্থিত হব?"},
"56:48" : {s: 56, a: 48, e: "And our earliest forefathers too?&rsquo;", b: "এবং আমাদের পূর্বপুরুষগণও!"},
"56:49" : {s: 56, a: 49, e: "Say [Prophet], &lsquo;The earliest and latest generations", b: "বলুনঃ পূর্ববর্তী ও পরবর্তীগণ,"},
"56:50" : {s: 56, a: 50, e: "will all be gathered on a predetermined Day", b: "সবাই একত্রিত হবে এক নির্দিষ্ট দিনের নির্দিষ্ট সময়ে।"},
});
