window.translation = $.extend(window.translation || {}, {
"56:77" : {s: 56, a: 77, e: "that this is truly a noble Quran,", b: "নিশ্চয় এটা সম্মানিত কোরআন,"},
"56:78" : {s: 56, a: 78, e: "in a protected Record", b: "যা আছে এক গোপন কিতাবে,"},
"56:79" : {s: 56, a: 79, e: "that only the purified can touch,", b: "যারা পাক-পবিত্র, তারা ব্যতীত অন্য কেউ একে স্পর্শ করবে না।"},
"56:80" : {s: 56, a: 80, e: "sent down from the Lord of all being.", b: "এটা বিশ্ব-পালনকর্তার পক্ষ থেকে অবতীর্ণ।"},
"56:81" : {s: 56, a: 81, e: "How can you scorn this statement?", b: "তবুও কি তোমরা এই বাণীর প্রতি শৈথিল্য পদর্শন করবে?"},
"56:82" : {s: 56, a: 82, e: "And how, in return for the livelihood you are given, can you deny it?", b: "এবং একে মিথ্যা বলাকেই তোমরা তোমাদের ভূমিকায় পরিণত করবে?"},
"56:83" : {s: 56, a: 83, e: "When the soul of a dying man comes up to his throat", b: "অতঃপর যখন কারও প্রাণ কন্ঠাগত হয়।"},
"56:84" : {s: 56, a: 84, e: "while you gaze on-", b: "এবং তোমরা তাকিয়ে থাক,"},
"56:85" : {s: 56, a: 85, e: "We are nearer to him than you, though you do not see Us-", b: "তখন আমি তোমাদের অপেক্ষা তার অধিক নিকটে থাকি; কিন্তু তোমরা দেখ না।"},
"56:86" : {s: 56, a: 86, e: "why, if you are not to be judged,", b: "যদি তোমাদের হিসাব-কিতাব না হওয়াই ঠিক হয়,"},
"56:87" : {s: 56, a: 87, e: "do you not restore his soul to him, if what you say is true?", b: "তবে তোমরা এই আত্মাকে ফিরাও না কেন, যদি তোমরা সত্যবাদী হও ?"},
"56:88" : {s: 56, a: 88, e: "If that dying person is one of those who will be brought near to God,", b: "যদি সে নৈকট্যশীলদের একজন হয়;"},
"56:89" : {s: 56, a: 89, e: "he will have rest, ease, and a Garden of Bliss;", b: "তবে তার জন্যে আছে সুখ, উত্তম রিযিক এবং নেয়ামতে ভরা উদ্যান।"},
"56:90" : {s: 56, a: 90, e: "if he is one of those on the Right,", b: "আর যদি সে ডান পার্শ্বস্থদের একজন হয়,"},
"56:91" : {s: 56, a: 91, e: "[he will hear], &lsquo;Peace be on you,&rsquo; from his companions on the Right;", b: "তবে তাকে বলা হবেঃ তোমার জন্যে ডানপার্শ্বসস্থদের পক্ষ থেকে সালাম।"},
"56:92" : {s: 56, a: 92, e: "but if he is one of those who denied the truth and went astray,", b: "আর যদি সে পথভ্রষ্ট মিথ্যারোপকারীদের একজন হয়,"},
"56:93" : {s: 56, a: 93, e: "he will be welcomed with scalding water.", b: "তবে তার আপ্যায়ন হবে উত্তপ্ত পানি দ্বারা।"},
"56:94" : {s: 56, a: 94, e: "He will burn in Hell.", b: "এবং সে নিক্ষিপ্ত হবে অগ্নিতে।"},
"56:95" : {s: 56, a: 95, e: "This is the certain truth:", b: "এটা ধ্রুব সত্য।"},
"56:96" : {s: 56, a: 96, e: "[Prophet], glorify the name of your Lord the Supreme.", b: "অতএব, আপনি আপনার মহান পালনকর্তার নামে পবিত্রতা ঘোষণা করুন।"},
"57:1" : {s: 57, a: 1, e: "Everything in the heavens and earth glorifies God&ndash;&ndash;He is the Almighty, the Wise.", b: "নভোমন্ডল ও ভূমন্ডলে যা কিছু আছে, সবাই আল্লাহর পবিত্রতা ঘোষণা করে। তিনি শক্তিধর; প্রজ্ঞাময়।"},
"57:2" : {s: 57, a: 2, e: "Control of the heavens and earth belongs to Him; He gives life and death; He has power over all things.", b: "নভোমন্ডল ও ভূমন্ডলের রাজত্ব তাঁরই। তিনি জীবন দান করেন ও মৃত্যু ঘটান। তিনি সবকিছু করতে সক্ষম।"},
"57:3" : {s: 57, a: 3, e: "He is the First and the Last; the Outer and the Inner; He has knowledge of all things.", b: "তিনিই প্রথম, তিনিই সর্বশেষ, তিনিই প্রকাশমান ও অপ্রকাশমান এবং তিনি সব বিষয়ে সম্যক পরিজ্ঞাত।"},
});
