window.translation = $.extend(window.translation || {}, {
"77:20" : {s: 77, a: 20, e: "Did We not make you from an underrated fluid", b: "আমি কি তোমাদেরকে তুচ্ছ পানি থেকে সৃষ্টি করিনি?"},
"77:21" : {s: 77, a: 21, e: "which We housed in a safe lodging", b: "অতঃপর আমি তা রেখেছি এক সংরক্ষিত আধারে,"},
"77:22" : {s: 77, a: 22, e: "for a determined period?", b: "এক নির্দিষ্টকাল পর্যন্ত,"},
"77:23" : {s: 77, a: 23, e: "We determine [it]: how excellently We determine!", b: "অতঃপর আমি পরিমিত আকারে সৃষ্টি করেছি, আমি কত সক্ষম স্রষ্টা?"},
"77:24" : {s: 77, a: 24, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:25" : {s: 77, a: 25, e: "Did We not make the earth a home", b: "আমি কি পৃথিবীকে সৃষ্টি করিনি ধারণকারিণীরূপে,"},
"77:26" : {s: 77, a: 26, e: "for the living and the dead?", b: "জীবিত ও মৃতদেরকে?"},
"77:27" : {s: 77, a: 27, e: "Did We not place firm, lofty mountains on it and provide you with sweet water?", b: "আমি তাতে স্থাপন করেছি মজবুত সুউচ্চ পর্বতমালা এবং পান করিয়েছি তোমাদেরকে তৃষ্ণা নিবারণকারী সুপেয় পানি।"},
"77:28" : {s: 77, a: 28, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:29" : {s: 77, a: 29, e: "They will be told, &lsquo;Go to that which you used to deny!", b: "চল তোমরা তারই দিকে, যাকে তোমরা মিথ্যা বলতে।"},
"77:30" : {s: 77, a: 30, e: "Go to a shadow of smoke!&rsquo; It rises in three columns;", b: "চল তোমরা তিন কুন্ডলীবিশিষ্ট ছায়ার দিকে,"},
"77:31" : {s: 77, a: 31, e: "no shade does it give, nor relief from the flame;", b: "যে ছায়া সুনিবিড় নয় এবং অগ্নির উত্তাপ থেকে রক্ষা করে না।"},
"77:32" : {s: 77, a: 32, e: "it shoots out sparks as large as treetrunks", b: "এটা অট্টালিকা সদৃশ বৃহৎ স্ফুলিংগ নিক্ষেপ করবে।"},
"77:33" : {s: 77, a: 33, e: "and as bright as copper.", b: "যেন সে পীতবর্ণ উষ্ট্রশ্রেণী।"},
"77:34" : {s: 77, a: 34, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:35" : {s: 77, a: 35, e: "On that Day they will be speechless,", b: "এটা এমন দিন, যেদিন কেউ কথা বলবে না।"},
"77:36" : {s: 77, a: 36, e: "and they will be given no chance to offer any excuses.", b: "এবং কাউকে তওবা করার অনুমতি দেয়া হবে না।"},
"77:37" : {s: 77, a: 37, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:38" : {s: 77, a: 38, e: "[They will be told], &lsquo;This is the Day of Decision: We have gathered you and earlier generations.", b: "এটা বিচার দিবস, আমি তোমাদেরকে এবং তোমাদের পূর্ববর্তীদেরকে একত্রিত করেছি।"},
"77:39" : {s: 77, a: 39, e: "If you have any plots against Me, try them now.&rsquo;", b: "অতএব, তোমাদের কোন অপকৌশল থাকলে তা প্রয়োগ কর আমার কাছে।"},
"77:40" : {s: 77, a: 40, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:41" : {s: 77, a: 41, e: "But those who took heed of God will enjoy cool shade, springs,", b: "নিশ্চয় খোদাভীরুরা থাকবে ছায়ায় এবং প্রস্রবণসমূহে-"},
"77:42" : {s: 77, a: 42, e: "and any fruit they desire;", b: "এবং তাদের বাঞ্ছিত ফল-মূলের মধ্যে।"},
"77:43" : {s: 77, a: 43, e: "[they will be told], &lsquo;Eat and drink to your hearts&rsquo; content as a reward for your deeds:", b: "বলা হবেঃ তোমরা যা করতে তার বিনিময়ে তৃপ্তির সাথে পানাহার কর।"},
"77:44" : {s: 77, a: 44, e: "this is how We reward those who do good.&rsquo;", b: "এভাবেই আমি সৎকর্মশীলদেরকে পুরস্কৃত করে থাকি।"},
"77:45" : {s: 77, a: 45, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:46" : {s: 77, a: 46, e: "[You may] eat and enjoy yourselves for a short while, evildoers that you are.", b: "কাফেরগণ, তোমরা কিছুদিন খেয়ে নাও এবং ভোগ করে নাও। তোমরা তো অপরাধী।"},
"77:47" : {s: 77, a: 47, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:48" : {s: 77, a: 48, e: "When they are told, &lsquo;Bow down in prayer,&rsquo; they do not do so.", b: "যখন তাদেরকে বলা হয়, নত হও, তখন তারা নত হয় না।"},
"77:49" : {s: 77, a: 49, e: "Woe, on that Day, to those who denied the truth!", b: "সেদিন মিথ্যারোপকারীদের দুর্ভোগ হবে।"},
"77:50" : {s: 77, a: 50, e: "In what revelation, after this, will they believe?", b: "এখন কোন কথায় তারা এরপর বিশ্বাস স্থাপন করবে?"},
});
