window.translation = $.extend(window.translation || {}, {
"81:1" : {s: 81, a: 1, e: "When the sun is shrouded in darkness,", b: "যখন সূর্য আলোহীন হয়ে যাবে,", t: "بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ إِذَا ٱلشَّمْسُ كُوِّرَتْ"},
"81:2" : {s: 81, a: 2, e: "when the stars are dimmed,", b: "যখন নক্ষত্র মলিন হয়ে যাবে,", t: "وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتْ"},
"81:3" : {s: 81, a: 3, e: "when the mountains are set in motion,", b: "যখন পর্বতমালা অপসারিত হবে,", t: "وَإِذَا ٱلْجِبَالُ سُيِّرَتْ"},
"81:4" : {s: 81, a: 4, e: "when pregnant camels are abandoned,", b: "যখন দশ মাসের গর্ভবতী উষ্ট্রীসমূহ উপেক্ষিত হবে;", t: "وَإِذَا ٱلْعِشَارُ عُطِّلَتْ"},
"81:5" : {s: 81, a: 5, e: "when wild beasts are herded together,", b: "যখন বন্য পশুরা একত্রিত হয়ে যাবে,", t: "وَإِذَا ٱلْوُحُوشُ حُشِرَتْ"},
"81:6" : {s: 81, a: 6, e: "when the seas boil over,", b: "যখন সমুদ্রকে উত্তাল করে তোলা হবে,", t: "وَإِذَا ٱلْبِحَارُ سُجِّرَتْ"},
"81:7" : {s: 81, a: 7, e: "when souls are sorted into classes,", b: "যখন আত্মাসমূহকে যুগল করা হবে,", t: "وَإِذَا ٱلنُّفُوسُ زُوِّجَتْ"},
"81:8" : {s: 81, a: 8, e: "when the baby girl buried alive is asked", b: "যখন জীবন্ত প্রোথিত কন্যাকে জিজ্ঞেস করা হবে,", t: "وَإِذَا ٱلْمَوْءُۥدَةُ سُئِلَتْ"},
"81:9" : {s: 81, a: 9, e: "for what sin she was killed,", b: "কি অপরাধে তাকে হত্য করা হল?", t: "بِأَىِّ ذَنۢبٍ قُتِلَتْ"},
"81:10" : {s: 81, a: 10, e: "when the records of deeds are spread open,", b: "যখন আমলনামা খোলা হবে,", t: "وَإِذَا ٱلصُّحُفُ نُشِرَتْ"},
"81:11" : {s: 81, a: 11, e: "when the sky is stripped away,", b: "যখন আকাশের আবরণ অপসারিত হবে,", t: "وَإِذَا ٱلسَّمَآءُ كُشِطَتْ"},
"81:12" : {s: 81, a: 12, e: "when Hell is made to blaze", b: "যখন জাহান্নামের অগ্নি প্রজ্বলিত করা হবে", t: "وَإِذَا ٱلْجَحِيمُ سُعِّرَتْ"},
"81:13" : {s: 81, a: 13, e: "and Paradise brought near:", b: "এবং যখন জান্নাত সন্নিকটবর্তী হবে,", t: "وَإِذَا ٱلْجَنَّةُ أُزْلِفَتْ"},
"81:14" : {s: 81, a: 14, e: "then every soul will know what it has brought about.", b: "তখন প্রত্যেকেই জেনে নিবে সে কি উপস্থিত করেছে।", t: "عَلِمَتْ نَفْسٌ مَّآ أَحْضَرَتْ"},
"81:15" : {s: 81, a: 15, e: "I swear by the planets", b: "আমি শপথ করি যেসব নক্ষত্রগুলো পশ্চাতে সরে যায়।", t: "فَلَآ أُقْسِمُ بِٱلْخُنَّسِ"},
"81:16" : {s: 81, a: 16, e: "that recede, move, and hide,", b: "চলমান হয় ও অদৃশ্য হয়,", t: "ٱلْجَوَارِ ٱلْكُنَّسِ"},
"81:17" : {s: 81, a: 17, e: "by the night that descends,", b: "শপথ নিশাবসান ও", t: "وَٱلَّيْلِ إِذَا عَسْعَسَ"},
"81:18" : {s: 81, a: 18, e: "by the dawn that softly breathes:", b: "প্রভাত আগমন কালের,", t: "وَٱلصُّبْحِ إِذَا تَنَفَّسَ"},
"81:19" : {s: 81, a: 19, e: "this is the speech of a noble messenger,", b: "নিশ্চয় কোরআন সম্মানিত রসূলের আনীত বাণী,", t: "إِنَّهُۥ لَقَوْلُ رَسُولٍ كَرِيمٍ"},
"81:20" : {s: 81, a: 20, e: "who possesses great strength and is held in honour by the Lord of the Throne&ndash;&ndash;", b: "যিনি শক্তিশালী, আরশের মালিকের নিকট মর্যাদাশালী,", t: "ذِى قُوَّةٍ عِندَ ذِى ٱلْعَرْشِ مَكِينٍ"},
"81:21" : {s: 81, a: 21, e: "he is obeyed there and worthy of trust.", b: "সবার মান্যবর, সেখানকার বিশ্বাসভাজন।", t: "مُّطَاعٍ ثَمَّ أَمِينٍ"},
"81:22" : {s: 81, a: 22, e: "Your companion is not mad:", b: "এবং তোমাদের সাথী পাগল নন।", t: "وَمَا صَاحِبُكُم بِمَجْنُونٍ"},
"81:23" : {s: 81, a: 23, e: "he did see him on the clear horizon.", b: "তিনি সেই ফেরেশতাকে প্রকাশ্য দিগন্তে দেখেছেন।", t: "وَلَقَدْ رَءَاهُ بِٱلْأُفُقِ ٱلْمُبِينِ"},
"81:24" : {s: 81, a: 24, e: "He does not withhold what is revealed to him from beyond.", b: "তিনি অদৃশ্য বিষয় বলতে কৃপনতা করেন না।", t: "وَمَا هُوَ عَلَى ٱلْغَيْبِ بِضَنِينٍ"},
"81:25" : {s: 81, a: 25, e: "This is not the word of an outcast devil.", b: "এটা বিতাড়িত শয়তানের উক্তি নয়।", t: "وَمَا هُوَ بِقَوْلِ شَيْطَٰنٍ رَّجِيمٍ"},
"81:26" : {s: 81, a: 26, e: "So where are you [people] going?", b: "অতএব, তোমরা কোথায় যাচ্ছ?", t: "فَأَيْنَ تَذْهَبُونَ"},
"81:27" : {s: 81, a: 27, e: "This is a message for all people;", b: "এটা তো কেবল বিশ্বাবাসীদের জন্যে উপদেশ,", t: "إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَٰلَمِينَ"},
"81:28" : {s: 81, a: 28, e: "for those who wish to take the straight path.", b: "তার জন্যে, যে তোমাদের মধ্যে সোজা চলতে চায়।", t: "لِمَن شَآءَ مِنكُمْ أَن يَسْتَقِيمَ"},
"81:29" : {s: 81, a: 29, e: "But you will only wish to do so by the will of God, the Lord of all people.", b: "তোমরা আল্লাহ রাব্বুল আলামীনের অভিপ্রায়ের বাইরে অন্য কিছুই ইচ্ছা করতে পার না।", t: "وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّ ٱلْعَٰلَمِينَ"},
});
