window.translation = $.extend(window.translation || {}, {
"86:1" : {s: 86, a: 1, e: "By the sky and the night-comer&ndash;&ndash;", b: "শপথ আকাশের এবং রাত্রিতে আগমনকারীর।"},
"86:2" : {s: 86, a: 2, e: "What will explain to you what the night-comer is?", b: "আপনি জানেন, যে রাত্রিতে আসে সেটা কি?"},
"86:3" : {s: 86, a: 3, e: "The piercing star&ndash;&ndash;", b: "সেটা এক উজ্জ্বল নক্ষত্র।"},
"86:4" : {s: 86, a: 4, e: "there is a watcher over every soul.", b: "প্রত্যেকের উপর একজন তত্ত্বাবধায়ক রয়েছে।"},
"86:5" : {s: 86, a: 5, e: "Man should reflect on what he was created from.", b: "অতএব, মানুষের দেখা উচিত কি বস্তু থেকে সে সৃজিত হয়েছে।"},
"86:6" : {s: 86, a: 6, e: "He is created from spurting fluid,", b: "সে সৃজিত হয়েছে সবেগে স্খলিত পানি থেকে।"},
"86:7" : {s: 86, a: 7, e: "then he emerges from between the backbone and breastbone:", b: "এটা নির্গত হয় মেরুদন্ড ও বক্ষপাজরের মধ্য থেকে।"},
"86:8" : {s: 86, a: 8, e: "God is certainly able to bring him back to life.", b: "নিশ্চয় তিনি তাকে ফিরিয়ে নিতে সক্ষম।"},
"86:9" : {s: 86, a: 9, e: "On the Day when secrets are laid bare", b: "যেদিন গোপন বিষয়াদি পরীক্ষিত হবে,"},
"86:10" : {s: 86, a: 10, e: "he will have no power and no one to help him.", b: "সেদিন তার কোন শক্তি থাকবে না এবং সাহায্যকারীও থাকবে না।"},
"86:11" : {s: 86, a: 11, e: "By the sky and its recurring rain,", b: "শপথ চক্রশীল আকাশের"},
"86:12" : {s: 86, a: 12, e: "by the earth that cracks open!", b: "এবং বিদারনশীল পৃথিবীর"},
"86:13" : {s: 86, a: 13, e: "This is truly a decisive statement;", b: "নিশ্চয় কোরআন সত্য-মিথ্যার ফয়সালা।"},
"86:14" : {s: 86, a: 14, e: "it is not something to be taken lightly.", b: "এবং এটা উপহাস নয়।"},
"86:15" : {s: 86, a: 15, e: "They plot and scheme,", b: "তারা ভীষণ চক্রান্ত করে,"},
"86:16" : {s: 86, a: 16, e: "but so do I:", b: "আর আমিও কৌশল করি।"},
"86:17" : {s: 86, a: 17, e: "[Prophet], let the disbelievers be, let them be for a while.", b: "অতএব, কাফেরদেরকে অবকাশ দিন, তাদেরকে অবকাশ দিন, কিছু দিনের জন্যে।"},
"87:1" : {s: 87, a: 1, e: "[Prophet], glorify the name of your Lord the Most High,", b: "আপনি আপনার মহান পালনকর্তার নামের পবিত্রতা বর্ণনা করুন"},
"87:2" : {s: 87, a: 2, e: "who created [all things] in due proportion;", b: "যিনি সৃষ্টি করেছেন ও সুবিন্যস্ত করেছেন।"},
"87:3" : {s: 87, a: 3, e: "who determined their destinies and guided them;", b: "এবং যিনি সুপরিমিত করেছেন ও পথ প্রদর্শন করেছেন"},
"87:4" : {s: 87, a: 4, e: "who brought out the green pasture", b: "এবং যিনি তৃণাদি উৎপন্ন করেছেন,"},
"87:5" : {s: 87, a: 5, e: "then made it dark debris.", b: "অতঃপর করেছেন তাকে কাল আবর্জনা।"},
"87:6" : {s: 87, a: 6, e: "[Prophet], We shall teach you [the Quran] and you will not forget&ndash;&ndash;", b: "আমি আপনাকে পাঠ করাতে থাকব, ফলে আপনি বিস্মৃত হবেন না"},
"87:7" : {s: 87, a: 7, e: "unless God wishes; He knows both what is open and what is hidden&ndash;&ndash;", b: "আল্লাহ যা ইচ্ছা করেন তা ব্যতীত। নিশ্চয় তিনি জানেন প্রকাশ্য ও গোপন বিষয়।"},
"87:8" : {s: 87, a: 8, e: "We shall show you the easy way.", b: "আমি আপনার জন্যে সহজ শরীয়ত সহজতর করে দেবো।"},
"87:9" : {s: 87, a: 9, e: "So remind, if reminding will help.", b: "উপদেশ ফলপ্রসূ হলে উপদেশ দান করুন,"},
"87:10" : {s: 87, a: 10, e: "Those who stand in awe of God will heed the reminder,", b: "যে ভয় করে, সে উপদেশ গ্রহণ করবে,"},
"87:11" : {s: 87, a: 11, e: "but it will be ignored by the most wicked,", b: "আর যে, হতভাগা, সে তা উপেক্ষা করবে,"},
"87:12" : {s: 87, a: 12, e: "who will enter the Great Fire,", b: "সে মহা-অগ্নিতে প্রবেশ করবে।"},
"87:13" : {s: 87, a: 13, e: "where they will neither die nor live.", b: "অতঃপর সেখানে সে মরবেও না, জীবিতও থাকবে না।"},
"87:14" : {s: 87, a: 14, e: "Prosperous are those who purify themselves,", b: "নিশ্চয় সাফল্য লাভ করবে সে, যে শুদ্ধ হয়"},
"87:15" : {s: 87, a: 15, e: "remember the name of their Lord, and pray.", b: "এবং তার পালনকর্তার নাম স্মরণ করে, অতঃপর নামায আদায় করে।"},
});
