window.translation = $.extend(window.translation || {}, {
"97:1" : {s: 97, a: 1, e: "We sent it down on the Night of Glory.", b: "আমি একে নাযিল করেছি শবে-কদরে।", t: "بِّسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ إِنَّآ أَنزَلْنَٰهُ فِى لَيْلَةِ ٱلْقَدْرِ"},
"97:2" : {s: 97, a: 2, e: "What will explain to you what that Night of Glory is?", b: "শবে-কদর সমন্ধে আপনি কি জানেন?", t: "وَمَآ أَدْرَىٰكَ مَا لَيْلَةُ ٱلْقَدْرِ"},
"97:3" : {s: 97, a: 3, e: "The Night of Glory is better than a thousand months;", b: "শবে-কদর হল এক হাজার মাস অপেক্ষা শ্রেষ্ঠ।", t: "لَيْلَةُ ٱلْقَدْرِ خَيْرٌ مِّنْ أَلْفِ شَهْرٍ"},
"97:4" : {s: 97, a: 4, e: "on that night the angels and the Spirit descend again and again with their Lord&rsquo;s permission on every task;", b: "এতে প্রত্যেক কাজের জন্যে ফেরেশতাগণ ও রূহ অবতীর্ণ হয় তাদের পালনকর্তার নির্দেশক্রমে।", t: "تَنَزَّلُ ٱلْمَلَٰٓئِكَةُ وَٱلرُّوحُ فِيهَا بِإِذْنِ رَبِّهِم مِّن كُلِّ أَمْرٍ"},
"97:5" : {s: 97, a: 5, e: "[there is] peace that night until the break of dawn.", b: "এটা নিরাপত্তা, যা ফজরের উদয় পর্যন্ত অব্যাহত থাকে।", t: "سَلَٰمٌ هِىَ حَتَّىٰ مَطْلَعِ ٱلْفَجْرِ"},
"98:1" : {s: 98, a: 1, e: "Those who disbelieve among the People of the Book and the idolaters were not about to change their ways until they were sent clear evidence,", b: "আহলে-কিতাব ও মুশরেকদের মধ্যে যারা কাফের ছিল, তারা প্রত্যাবর্তন করত না যতক্ষণ না তাদের কাছে সুস্পষ্ট প্রমাণ আসত।", t: "بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ لَمْ يَكُنِ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَٰبِ وَٱلْمُشْرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأْتِيَهُمُ ٱلْبَيِّنَةُ"},
"98:2" : {s: 98, a: 2, e: "a messenger from God, reading out pages [blessed with] purity,", b: "অর্থাৎ আল্লাহর একজন রসূল, যিনি আবৃত্তি করতেন পবিত্র সহীফা,", t: "رَسُولٌ مِّنَ ٱللَّهِ يَتْلُوا۟ صُحُفًا مُّطَهَّرَةً"},
"98:3" : {s: 98, a: 3, e: "containing true scriptures.", b: "যাতে আছে, সঠিক বিষয়বস্তু।", t: "فِيهَا كُتُبٌ قَيِّمَةٌ"},
"98:4" : {s: 98, a: 4, e: "[Yet] those who were given the Scripture became divided only after they were sent [such] clear evi-dence", b: "অপর কিতাব প্রাপ্তরা যে বিভ্রান্ত হয়েছে, তা হয়েছে তাদের কাছে সুস্পষ্ট প্রমাণ আসার পরেই।", t: "وَمَا تَفَرَّقَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَٰبَ إِلَّا مِنۢ بَعْدِ مَا جَآءَتْهُمُ ٱلْبَيِّنَةُ"},
"98:5" : {s: 98, a: 5, e: "though all they are ordered to do is worship God alone, sincerely devoting their religion to Him as people of true faith, keep up the prayer, and pay the prescribed alms, for that is the true religion.", b: "তাদেরকে এছাড়া কোন নির্দেশ করা হয়নি যে, তারা খাঁটি মনে একনিষ্ঠভাবে আল্লাহর এবাদত করবে, নামায কায়েম করবে এবং যাকাত দেবে। এটাই সঠিক ধর্ম।", t: "وَمَآ أُمِرُوٓا۟ إِلَّا لِيَعْبُدُوا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ حُنَفَآءَ وَيُقِيمُوا۟ ٱلصَّلَوٰةَ وَيُؤْتُوا۟ ٱلزَّكَوٰةَ ۚ وَذَٰلِكَ دِينُ ٱلْقَيِّمَةِ"},
"98:6" : {s: 98, a: 6, e: "Those who disbelieve among the People of the Book and the idolaters will have the Fire of Hell, there to remain. They are the worst of creation.", b: "আহলে-কিতাব ও মুশরেকদের মধ্যে যারা কাফের, তারা জাহান্নামের আগুনে স্থায়ীভাবে থাকবে। তারাই সৃষ্টির অধম।", t: "إِنَّ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَٰبِ وَٱلْمُشْرِكِينَ فِى نَارِ جَهَنَّمَ خَٰلِدِينَ فِيهَآ ۚ أُو۟لَٰٓئِكَ هُمْ شَرُّ ٱلْبَرِيَّةِ"},
"98:7" : {s: 98, a: 7, e: "Those who believe and do good deeds are the best of creation.", b: "যারা ঈমান আনে ও সৎকর্ম করে, তারাই সৃষ্টির সেরা।", t: "إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ أُو۟لَٰٓئِكَ هُمْ خَيْرُ ٱلْبَرِيَّةِ"},
});
